mapper.select:
SELECT id, userId, password, nickName, createTime, updateTime, isWithDraw, isAdmin, status
FROM board_user
WHERE id = #{id}

mapper.update:
UPDATE board_user
SET password=#{password},
nickName=#{nickName},
isWithDraw=#{isWithDraw},
status=#{status}
WHERE id = #{id}

mapper.select:
SELECT COUNT(id)
FROM board_user
WHERE userId = #{userId}

mapper.update:
UPDATE board_user
SET passWord = #{password}
WHERE userId = #{userId}
